import { ParamHaterComponent } from "./param-hater.component";
import { RouterTestingHarness } from "@angular/router/testing";
import { Routes } from "@angular/router";

const routes: Routes = [{ path: 'param-hater/:id', component: ParamHaterComponent }];
const router = new RouterTestingHarness(routes);
const advice = 'You should not pass';

describe('ParamHaterComponent', () => {
  //Use RouterTestingHarness
  let router: RouterTestingHarness;
  let component: ParamHaterComponent;

  it('should display advice id', () => {
    //Arrange
    router = new RouterTestingHarness(routes);
    component = new ParamHaterComponent(router);
    //Act
    component.ngOnInit();
    //Assert
    expect(component.advice).toBe('You should not pass');
   });
});
