describe('ParamHaterComponent', () => {
  //Use RouterTestingHarness
  it('should display advice id', () => {
    // Arrange
    const fixture = TestBed.createComponent(ParamHaterComponent);
    const component = fixture.componentInstance;
    const adviceId = '123';
    component.adviceId = adviceId;
    fixture.detectChanges();
    const element = fixture.debugElement.query(By.css('.advice-id'));

    // Act
    const text = element.nativeElement.textContent;

    // Assert
    expect(text).toContain(adviceId + ' is a terrible advice id');
  });
});
