import { ComponentFixture, TestBed, waitForAsync } from '@angular/core/testing';
import { Router } from '@angular/router';
import { ParamHaterComponent } from './param-hater.component';

describe('ParamHaterComponent', () => {
  let component: ParamHaterComponent;
  let fixture: ComponentFixture<ParamHaterComponent>;
  let router: Router;

  beforeEach(waitForAsync(() => {
    TestBed.configureTestingModule({
      imports: [ParamHaterComponent], // Import the standalone component here
      providers: [
        { provide: Router, useValue: { navigate: jasmine.createSpy('navigate') } },
      ]
    }).compileComponents();
  }));

  beforeEach(() => {
    fixture = TestBed.createComponent(ParamHaterComponent);
    component = fixture.componentInstance;
    router = TestBed.inject(Router);
    fixture.detectChanges();
  });

  it('should display advice id', waitForAsync(() => {
    fixture.whenStable().then(() => {
      fixture.detectChanges();
      const compiled = fixture.nativeElement;
      expect(compiled.querySelector('.advice-id').textContent).toContain('123');
    });
  }));

  it('should redirect to the hate page', () => {
    component.nav();
    expect(router.navigate).toHaveBeenCalledWith(['/home']);
  });
});
