import { ComponentFixture, TestBed } from '@angular/core/testing';
import { RouterTestingHarness } from '@angular/router/testing';
import { ActivatedRoute } from '@angular/router';
import { ParamHaterComponent } from './param-hater.component';
import { of } from 'rxjs';

describe('ParamHaterComponent', () => {
  let component: ParamHaterComponent;
  let fixture: ComponentFixture<ParamHaterComponent>;

  beforeEach(async () => {
    await TestBed.configureTestingModule({
      declarations: [ParamHaterComponent],
      imports: [RouterTestingHarness],
      providers: [
        {
          provide: ActivatedRoute,
          useValue: {
            paramMap: of({
              get: (key: string) => '123' // Mocking the advice ID
            })
          }
        }
      ]
    }).compileComponents();

    fixture = TestBed.createComponent(ParamHaterComponent);
    component = fixture.componentInstance;
    fixture.detectChanges();
  });

  it('should display advice id', () => {
    const compiled = fixture.nativeElement;
    expect(compiled.querySelector('.advice-id').textContent).toContain('123');
  });
});
